import "./App.css";
import Data from "./components/Data";

function App() {
  const handleDelete = (index, e) => {
    document.getElementById("ROWS").remove(e);
    let count=10;
  };

  return (
    <div class="row justify-content-center">
      <div class="col-md-2">
        <ul class="list-group">
          <li class="list-group-item list-group-item-action">All Movies</li>
          <li class="list-group-item list-group-item-action">Action</li>
          <li class="list-group-item list-group-item-action">Anime</li>
          <li class="list-group-item list-group-item-action">Horror</li>
        </ul>
      </div>

      <div class="col-md-9">
        <h6>There are {Data.length} movies in the list</h6>

        <table class="table table-dark table-hover">
          <thead>
            <tr>
              <th scope="col">Title</th>
              <th scope="col">Genre</th>
              <th scope="col">Stock</th>
              <th scope="col">Rate</th>
              <th scope="col"> </th>
            </tr>
          </thead>
          <tbody>
            {Data.map((item, index) => {
              return (
                <tr key={index} id="ROWS">
                  <td>{item.title}</td>
                  <td>{item.genre}</td>
                  <td>{item.stock}</td>
                  <td>{item.rate}</td>
                  <td>
                    <button onClick={(e) => handleDelete(index, e)}>
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
            ;
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
