import { useState } from "react";
import "./App.css";
import Data from "./components/Data";

function App() {

  const [del,setDel] = useState([]);

const handleDelete = (index) => {
  setDel(del.filter((i) => i !== index));
};

  return (
    <div class="row justify-content-center">
      <div class="col-md-2">
        {Data.map((value) => {
          return (
            <ul class="list-group">
              <li class="list-group-item list-group-item-action">
                {value.genre}
              </li>
            </ul>
          );
        })}
      </div>

      <div class="col-md-9">
        <h5>There are {Data.length} movies in the list</h5>
        <table class="table table-dark table-hover">
          <thead>
            <tr>
              <th scope="col">Title</th>
              <th scope="col">Genre</th>
              <th scope="col">Stock</th>
              <th scope="col">Rate</th>
              <th scope="col"> </th>
            </tr>
          </thead>

          <tbody>
            {Data.map((item,index) => {
              return (
                  <tr key={index}>
                    <td>{item.title}</td>
                    <td>{item.genre}</td>
                    <td>{item.stock}</td>
                    <td>{item.rate}</td>
                    <td>
                      <button onClick={handleDelete(index)}>
                        Delete
                      </button>
                    </td>
                  </tr>
              );
            })}
            ;
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
