import React, { useState } from "react";
import Data from "./Data";

const TableTwo = () => {
    const [movie, setMovie] = useState(Data);
  let [count, setCount] = useState(Data.length);

  const handleDelete = (index, e) => {
    document.getElementById("ROWS").remove(e);
        console.log("Deleted X");
    setCount((count = count - 1));
  };

  const sortTitle = () => {
    const sortedData = Data.sort((a, b) => {
      if (a.title > b.title) return -1;
      if (a.title < b.title) return 1;
      return 0;
    });
    console.log(sortedData);
    setMovie();
  };

  return (
    <div>
      <h6>There are {count} movies in the list</h6>
      <table class="table table-dark table-hover">
        <thead>
          <tr>
            <th scope="col" onClick={sortTitle}>
              Title
            </th>
            <th scope="col">Genre</th>
            <th scope="col">Stock</th>
            <th scope="col">Rate</th>
            <th scope="col"> </th>
          </tr>
        </thead>
        <tbody>
          {Data.map((item, index) => {
            return (
              <tr key={index} id="ROWS">
                <td>{item.title}</td>
                <td>{item.genre}</td>
                <td>{item.stock}</td>
                <td>{item.rate}</td>
                <td>
                  <button onClick={(e) => handleDelete(index, e)}>
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
          ;
        </tbody>
      </table>
    </div>
  );
};
export default TableTwo;
